package org.springframework.demo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.beans.factory.annotation.Value;import org.springframework.context.annotation.PropertySource;import org.springframework.stereotype.Component;public class ATM {    private int idATM;    public ATM(int idATM) {        this.idATM = idATM;    }    public ATM(){}    public int getIdATM() {        return idATM;    }    public void setIdATM(int idATM) {        this.idATM = idATM;    }    public void printInfo(){        System.out.println("<----------------------------------------------->");        System.out.println("|  Hello this is the ATM number " + idATM +   "               |");        System.out.println("| 1.) Check balance                             |");        System.out.println("| 2.) Top up                                    |");        System.out.println("| 3.) Withdraw                                  |");        System.out.println("| 4.) Change PIN                                |");        System.out.println("| 0.) EXIT                                      |");        System.out.println("<----------------------------------------------->");    }}