package org.springframework.demo;import java.util.ArrayList;import java.util.List;public class Bank {    private List<User> users = new ArrayList<User>();    private List<User> userDebtor = new ArrayList<User>();    private static Bank bank;    public static  synchronized Bank getConnection(){        if(bank == null){            bank = new Bank();        }        return  bank;    }    public Bank(List<User> users, List<User> userDebtor) {        this.users = users;        this.userDebtor = userDebtor;    }    public Bank() {    }    public List<User> getUsers() {        return users;    }    public void setUsers(List<User> users) {        this.users = users;    }    public List<User> getUserDebtor() {        return userDebtor;    }    public void setUserDebtor(List<User> userDebtor) {        this.userDebtor = userDebtor;    }}