package org.springframework.demo;import org.springframework.context.support.ClassPathXmlApplicationContext;import org.springframework.stereotype.Component;import java.util.Scanner;@Component("top")public class TopUp implements BankService {    @Override    public void makeService(int num) {        ClassPathXmlApplicationContext context = new ClassPathXmlApplicationContext("applicationContext.xml");        Connection connection = context.getBean("dbc", Connection.class);        Scanner sc = new Scanner(System.in);        System.out.println("please add the number: ");        int a = sc.nextInt();        connection.changeBalance(a, num);    }}