package org.springframework.demo;//@Component("userBean")public class User {    private String name;    private String surname;    private  int cardNum;    private int balance;    private int pass;    public User(String name, String surname, int cardNum, int balance, int pass) {        this.name = name;        this.surname = surname;        this.cardNum = cardNum;        this.balance = balance;        this.pass = pass;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getSurname() {        return surname;    }    public void setSurname(String surname) {        this.surname = surname;    }    public int getCardNum() {        return cardNum;    }    public void setCardNum(int cardNum) {        this.cardNum = cardNum;    }    public int getBalance() {        return balance;    }    public void setBalance(int balance) {        this.balance = balance;    }    public int getPass() {        return pass;    }    public void sss(){        System.out.println("Hello");    }    public void setPass(int pass) {        this.pass = pass;    }    @Override    public String toString() {        return "User{" +                "name='" + name + '\'' +                ", surname='" + surname + '\'' +                ", card number=" + cardNum +                ", balance=" + balance +                ", pass=" + pass +                '}';    }}